mapper.update:
UPDATE schedule_log u
u.task_id=#{taskId}, u.pre_log_no=#{preLogNo}, u.progress=#{progress}, u.completed=#{completed}, u.update_time=NOW()
WHERE u.log_no=#{logNo} and status = 1;

UPDATE schedule_log u
u.progress=#{scheduleLogDo.progress}, u.completed=#{scheduleLogDo.completed}, u.update_time=NOW()
WHERE u.log_no=#{scheduleLogDo.logNo} and progress=#{oldProgress} and status=1;

mapper.select:
SELECT
*
FROM
schedule_log
WHERE
status=1

    AND     create_time >=#{startDate}


    AND     create_time <#{endDate}


    AND (name LIKE CONCAT('%',#{search},'%') OR ScheduleLogDo_name LIKE
    CONCAT('%',#{search},'%'))


    AND pid = #{pid}


    AND create_ScheduleLogDo = #{createScheduleLogDo}


    AND merchant_no = #{merchantNo}

order by create_time desc

    LIMIT #{page},#{pageSize}

SELECT
count(log_no) as count
FROM
schedule_log
WHERE
status=1

    AND     create_time >=#{startDate}


    AND     create_time <#{endDate}


    AND (name LIKE CONCAT('%',#{search},'%') OR ScheduleLogDo_name LIKE
    CONCAT('%',#{search},'%'))


    AND pid = #{pid}


    AND create_ScheduleLogDo = #{createScheduleLogDo}


    AND merchant_no = #{merchantNo}
